import { collection } from 'firebase/firestore'
import { db } from '../firebase';

const Book = () => {
  const memoriesRef = collection(db, "Memories");

    function onShareClick(e){
      e.preventDefault();
      console.log("Share clicked!");
    };

    return (
      <section className="Share">
        <h1> Share Memory </h1>
        <form id="create">
            <div className="container">
              <label htmlFor="leg-title"> Title: </label>
              <input type="text" id="title" name="title" placeholder="Enter game title..." /><br/>

              <label htmlFor="category"> Description: </label>
              <input type="text" id="category" name="category" placeholder="Enter game category..." /><br/>

              <label htmlFor="game-img">Image URL:</label>
              <input type="text" id="imageUrl" name="imageUrl" placeholder="Upload a photo..." /><br/>

              <input className="btn submit" type="submit" value="Share" onClick={onShareClick}/>
            </div>
        </form>
    </section>
  );

  
};

export default Book;
