import { useState, useEffect } from 'react';

import Header from './components/Header.js';
import Latest from './components/Latest.js';
import Book from './components/Book.js';
import Footer from './components/Footer';

import './App.css';
import '../src/styles/Header.css'
import '../src/styles/Latest.css'
import '../src/styles/Footer.css'

function App() {
  const [page, setPage] = useState('/Latest')

  const routes = {
    '/Latest': <Latest />,
    '/Book': <Book />,
  };

  const navigationChangeHandler = (path) =>{
    console.log(path);
    setPage(path);
  }

  const [memories, setMemories] = useState([]);
  const memoriesRef = collection(db, "Memories");

  useEffect(() => {
    const getMemories = async () =>{
      const data = await getDocs(memoriesRef);
      setMemories(data.docs.map((doc) => ({...doc.data(), id: doc.id})));
    }

    getMemories();
  }, []);

  return (
    <div className="App">
      <Header navigationChangeHandler={navigationChangeHandler}/>

      <main>
        {routes[page] || <h2> 404 Not Found! </h2> }
      </main>

      <section>
      {memories.map((memory) => {
        return <h1> {memory.Title} </h1>
      })}
    </section>
      <Footer />
    </div>
  );
}

export default App;
