import { useState } from 'react';
import firebase from "./firebase.js";

import Header from './components/Header.js';
import Latest from './components/Latest.js';
import Book from './components/Book.js';
import Footer from './components/Footer';
import './App.css';
import '../src/styles/Header.css'
import '../src/styles/Latest.css'
import '../src/styles/Footer.css'

function App() {
  const [page, setPage] = useState('/Latest')

  const [memories, setMemories] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("Memories");



  const routes = {
    '/Latest': <Latest />,
    '/Book': <Book />,
  };

  const navigationChangeHandler = (path) =>{
    console.log(path);
    setPage(path);
  }

  return (
    <div className="App">
      <Header navigationChangeHandler={navigationChangeHandler}/>
      <section>
        <h1>Latest Memories</h1>
        {memories.map((memory) => (
          console.log(memory)
        ))}
      </section>

      <main>
        {routes[page] || <h2> 404 Not Found! </h2> }
      </main>

      <Footer />
    </div>
  );
}

export default App;
