import { useState, useEffect } from 'react';
import { collection, getDocs } from 'firebase/firestore'
import { db } from '../firebase';

import LatestMemory from '../components/LatestMemory.js';

var slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
showSlides(slideIndex += n);
}
function currentSlide(n) {
showSlides(slideIndex = n);
}
function showSlides(n) {
var i;
var slides = document.getElementsByClassName("mySlides");
var dots = document.getElementsByClassName("dot");
if (n > slides.length) {slideIndex = 1}    
if (n < 1) {slideIndex = slides.length}
for (i = 0; i < slides.length; i++) {
slides[i].style.display = "none";  
}
for (i = 0; i < dots.length; i++) {
dots[i].className = dots[i].className.replace(" active", "");
}
slides[slideIndex-1].style.display = "block";  
dots[slideIndex-1].className += " active";
}

const Latest = () => {
  const [memories, setMemories] = useState([]);
  const memoriesRef = collection(db, "Memories");

  useEffect(() => {
    const getMemories = async () =>{
      const data = await getDocs(memoriesRef);
      setMemories(data.docs.map((doc) => ({...doc.data(), id: doc.id})));
    }

    getMemories();
  }, []);

    return (

      /*
      <section className="Latest">
        <h1>Latest Memories</h1>              
        <article className="Latest-Items">
          <ul className="Latest-Items-List">    
            {memories.map(memory => {
              return <LatestMemory key={memory.id} memory = {memory}/>
            })} 
          </ul>
        </article>
      </section>*/
          
      <section>
      <div class="slideshow-container">

<div class="mySlides fade">
  <div class="numbertext">1 / 3</div>
  <img src="img_nature_wide.jpg" alt="" width="100%"/>
  <div class="text">Caption Text</div>
</div>

<div class="mySlides fade">
  <div class="numbertext">2 / 3</div>
  <img src="img_snow_wide.jpg" alt="" width="100%"/>
  <div class="text">Caption Two</div>
</div>

<div class="mySlides fade">
  <div class="numbertext">3 / 3</div>
  <img src="img_mountains_wide.jpg" alt="" width="100%"/>
  <div class="text">Caption Three</div>
</div>

<a href class="prev" onclick="plusSlides(-1)">&#10094;</a>
<a href class="next" onclick="plusSlides(1)">&#10095;</a>

</div>
<br/>

<div >
  <span class="dot" onclick="currentSlide(1)"></span> 
  <span class="dot" onclick="currentSlide(2)"></span> 
  <span class="dot" onclick="currentSlide(3)"></span> 
</div>
</section>
  );
};


  
export default Latest;
